import React, { useState } from "react";
import { useNavigation } from "@react-navigation/native";
import { View, Text, ScrollView, StyleSheet, Alert } from "react-native";
import { Image, Input, Icon } from "@rneui/base";
import {
  Modal,
  Portal,
  Button,
  Card,
  Avatar,
  PaperProvider,
} from "react-native-paper";
import client from "../../../../kernel/http-client.gateway";


export default function PayScreen() {
  const navigation = useNavigation();
  const goProductoScreen = () => {
    navigation.navigate("homeStack");
  };

  const [visible, setVisible] = React.useState(false);
  const [email, setEmail] = useState("");
  const [error, setError] = useState({ email: "" });
  const [show, setShow] = useState(false);
  const [conektaData, setConektadata] = useState("");
  const showModal = () => setVisible(true);
  const hideModal = () => setVisible(false);
  const containerStyle = { backgroundColor: "white", padding: 20 };
  
  const pay = () => {
      console.log("Ya esta mandando el correo");
      setShow(true);
      setError({ email: "" });
      data = {
        "line_items": [{
          "name": "Nombre del Producto o Servicio",
          "unit_price": 23000,
          "quantity": 8
        }],
        "currency": "MXN",
        "customer_info": {
          "name": "Jorge Martínez",
          "email": email,
          "phone": "+5218181818181"
        },
        "metadata": {
          "datos_extra": "1234"
        },
        "charges": [{
          "payment_method": {
            "type": "cash",
            "expires_at": 1696686317
          }
        }]
      };
      try {
        client.doPost('/orders',data)
      } catch (error) {
        console.log('AAAAAAAaa')
      }
      
    }      
  

  const [visibleCard, setVisibleCard] = React.useState(false);

  const showModalCard = () => setVisibleCard(true);
  const hideModalCard = () => setVisibleCard(false);
  const containerStyleCard = { backgroundColor: "white", padding: 20 };

  return (
    <ScrollView contentContainerStyle={styles.scrollView}>
      <PaperProvider>
        <Portal>
          <Modal
            visible={visible}
            onDismiss={hideModal}
            contentContainerStyle={containerStyle}
          >
            <Text>PAGO EN EFECTIVO</Text>

            <View>
              <Image
                source={require("../../../../assets/oxxo_pay.png")}
                style={{ width: 250, height: 60, marginRight: 10 }}
              />
              <Input
                placeholder="Correo electronico"
                keyboardType="email-address"
                containerStyle={styles.input}
                onChange={(event) => setEmail(event.nativeEvent.text)}
                errorMessage={error.email}
                autoCapitalize="none"
                rightIcon={
                  <Icon type="material-community" name="email" size={22} />
                }
              />
              <Text style={{ textAlign: "justify" }}>
                Enviaremos una referencia de pago a tu correo para que puedas
                realizar el pago. Sigue las instrucciones del correo para
                completar tu compra.
              </Text>

              <Text style={{ color: "red", textAlign: "justify" }}>
                *La referencia solo dura una hora del día en curso
              </Text>
              
            </View>

            {/* Envuelve el bloque de texto y botones en un View */}
            <View style={{ marginVertical: 15 }}>
              <View
                style={{
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Button
                  mode="contained"
                  onPress={hideModal}
                  style={{ marginRight: 10 }}
                >
                  Cancelar
                </Button>
                <Button
                  mode="contained"
                  onPress={() => {
                    pay(); 
                    // Mostrar la alerta al hacer clic en el botón "Pagar"
                    Alert.alert(
                      "Referencia de pago enviada al correo electronico proporcionado",
                      "Hemos enviado la referencia de pago a tu correo electrónico, tienes un límite de una hora a partir del momento en que recibes el correo para realizar tu pago.",
                      [
                        {
                          text: "OK",
                          onPress: () => {
                            // Cierra el modal después de aceptar la alerta
                            goProductoScreen();

                            // Redirigir a la pantalla de Productos después de aceptar la alerta
                            navigation.navigate("homeStack"); // Reemplaza "Productos" con el nombre de tu pantalla de productos
                          },
                        },
                      ]
                    );
                  }}
                >
                  Pagar
                </Button>
              </View>
            </View>
          </Modal>
        </Portal>

        <Portal>
          <Modal
            visible={visibleCard}
            onDismiss={hideModalCard}
            contentContainerStyle={containerStyleCard}
          >
            <Text>PAGO CON TARJETA</Text>

            <View>
              <Text style={{ marginVertical: 10 }}>Nombre en la tarjeta</Text>
              <Input
                placeholder="Nombre como aparece en la tarjeta"
                containerStyle={styles.input}
                autoCapitalize="none"
              />
              <Text style={{ marginVertical: 10 }}>Número de tarjeta</Text>
              <Input
                placeholder="0000 0000 0000 0000"
                keyboardType="email-address"
                containerStyle={styles.input}
                autoCapitalize="none"
                rightIcon={
                  <View style={styles.iconContainer}>
                    <Icon
                      name="credit-card"
                      type="material-community"
                      size={24}
                      color="blue"
                    />
                  </View>
                }
              />
              <View
                style={{
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <View style={{ flex: 1 }}>
                  <Text style={{ marginVertical: 10 }}>Expedicion</Text>
                  <Input placeholder="AA/MM" autoCapitalize="none" />
                </View>
                <View style={{ flex: 1 }}>
                  <Text style={{ marginVertical: 10 }}>
                    Codigo de seguridad
                  </Text>
                  <Input placeholder="CCV" autoCapitalize="none" />
                </View>
              </View>
            </View>

            {/* Envuelve el bloque de texto y botones en un View */}
            <View style={{ marginVertical: 15 }}>
              <View
                style={{
                  flexDirection: "row",
                  justifyContent: "space-between",
                }}
              >
                <Button
                  mode="contained"
                  onPress={hideModalCard}
                  style={{ marginRight: 10 }}
                >
                  Cancelar
                </Button>
                <Button
                  mode="contained"
                  onPress={() => {
                    // Mostrar la alerta al hacer clic en el botón "Pagar"
                    Alert.alert(
                      "Pago realizado con exito",
                      "Gracias por comprar nuestros servicios y darnos tu confianza.",
                      [
                        {
                          text: "OK",
                          onPress: () => {
                            // Cierra el modal después de aceptar la alerta
                            hideModalCard();

                            // Redirigir a la pantalla de Productos después de aceptar la alerta
                            navigation.navigate("homeStack");
                          },
                        },
                      ]
                    );
                  }}
                >
                  Pagar
                </Button>
              </View>
            </View>
          </Modal>
        </Portal>

        <Card style={styles.card}>
          <Card.Title
            title="Tarjeta de crédito"
            subtitle="Saldo disponible: $36,000.00Mxn"
          />
          <Card.Content>
            <Text variant="titleLarge">Costo del servicio</Text>
            <Text variant="bodyMedium">$100.00Mx</Text>
          </Card.Content>
          <Card.Cover
            source={require("../../../../assets/TarjetaBnz.png")}
            style={styles.coverImage}
          />
        </Card>

        <Card style={styles.card}>
          <Card.Title
            title="Pagar con"
            subtitle={
              <View style={{ flexDirection: "row", alignItems: "center" }}>
                <Avatar.Image
                  source={{
                    uri: "data:image/png;base64,",
                  }}
                  size={24} // Ajusta el tamaño del avatar según tus necesidades
                />
                <Text style={{ marginLeft: 8 }}>Conekta</Text>
              </View>
            }
          />
          <Card.Content>
            <Text variant="titleLarge">Selecciona un método de pago</Text>
            <View
              style={{ paddingVertical: 20,flexDirection: "row", justifyContent: "space-between" }}
            >
              <Button  icon="cash" mode="contained" onPress={showModal}>
                Efectivo
              </Button>
              <Button
                icon="credit-card"
                mode="contained"
                onPress={showModalCard}
              >
                Tarjeta
              </Button>
            </View>
          </Card.Content>

        </Card>
      </PaperProvider>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  card: {
    backgroundColor: "#ECF8F8", // Cambia el color de fondo a gris
    borderWidth: 4,
    borderColor: "#93E1E5", // Cambia esto al color de borde que desees
    borderRadius: 10,
    overflow: "hidden",
    margin: 16, // Margen alrededor de la tarjeta
  },
  coverImage: {
    margin: 16, // Margen alrededor de la imagen
  },

  input: {
    width: "100%",
    marginVertical: 5,
  },
});
